import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = () => {
    // Here, you would typically send a login request to the backend for authentication.
    // For this example, we'll just do a simple client-side check.
    let user = "sachin"
    let paswd = "1234"

    if (username === user && password === paswd) {
      // Successful login
      navigate('/About'); // Replace '/dashboard' with the actual dashboard route
    } else {
      setError('Invalid username or password');
    }
  };

  return (
    <div className='container'>
      <h2>Login</h2>
      {error && <p>{error}</p>}
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default LoginPage;
