import React, { useState } from 'react';


export default function TextForm(prop) {
  const [text, setText] = useState("Enter text Here");
//   setText("vgdhbjafs");

    const HnadlUpClick = ()=>{
        // console.log("Button is click" + text)
        let newText = text.toUpperCase();
        setText (newText);

    }
    const handleOnChange = (event)=>{
        // console.log("onChange")
        setText(event.target.value);
    }

  return (
    <div>
      <h3>{prop.heading}</h3>
      <div className='mb-3'>
        <textarea className="postContent" value={text} onChange={handleOnChange} rows={8} cols={80}></textarea>
      </div>
      <button className="btn btn-primary"onClick={HnadlUpClick}>Convert to UpperCase</button>
      <div style={{ textAlign: 'right', marginTop: '10px' }}>
        <button className="btn btn-primary"onClick={HnadlUpClick}>Convert to LowerCase</button>
        </div>
      <h2>Your Text Summary</h2>
      <p>{text.split(" ").length} Words and {text.length} Charecters</p>
      <p>Can be read in {0.008 * text.split(" ").length} Minutes</p>
      <h4>Preview</h4>
      <p>{text}</p>
    </div>
  );
}
