import React from 'react';
import ReactApexChart from 'react-apexcharts';
import Emptable from './employee';

const PieChart = (props) => {
  const labelColor = props.mode === 'dark' ? 'white' : 'black';

  const options = {
    chart: {
      type: 'pie',
    },
    labels: ['Developers', 'Front-end', 'Testers', 'Others'],
    series: [35, 25, 40, 10],
    dataLabels: {
      style: {
        colors: [labelColor, labelColor, labelColor, labelColor],
      },
    },
  };

  return (
    <div>
      <Emptable />
      <h3>Company Employees Salary Performance</h3>
      <ReactApexChart options={options} series={options.series} type="pie" width="500" />
    </div>
  );
};

export default PieChart;
