import React, { useState } from 'react';
import ReactApexChart from 'react-apexcharts';

const PieChart = () => {
  const options = {
    chart: {
      type: 'pie',
    },
    labels: ['Devlopers', 'Frant-end', 'Testers', 'Others'],
    series: [35, 25, 40, 10], // The data percentages for each slice
  };

  return (
    <div>
      <h3>Company Employees over-all performance</h3>
      <ReactApexChart options={options} series={options.series} type="pie" width="500" />
    </div>
  );
};

export default function About() {
  const [myStyle, setmyStyle] = useState({
    color: "black",
    backgroundColor: "white",
  });

  const [btnTxt, setbtnTxt] = useState("Enable Darkmode");

  const DarkClick = () => {
    if (myStyle.color === "white") {
      setmyStyle({
        color: "black",
        backgroundColor: "white",
      });
      setbtnTxt("Enable Darkmode");
    } else {
      setmyStyle({
        color: "white",
        backgroundColor: "black",
      });
      setbtnTxt("Enable Litemode");
    }
  };

  return (
    <div className="container" style={myStyle}>
      <h2>About Us</h2>
      <div className="accordion" id="accordionExample">
        {/* ... */}
      </div>
      <div className="my-4">
        <button className="btn btn-primary" onClick={DarkClick}>
          {btnTxt}
        </button>
      </div>

      <PieChart /> {/* Render the PieChart component here */}
    </div>
  );
}
