import React, { useState } from 'react';


export default function TextForm(prop) {
  const [text, setText] = useState("Enter text Here");
//   setText("vgdhbjafs");

    const HandleUpClick = ()=>{
        // console.log("Button is click" + text)
        let newText = text.toUpperCase();
        setText (newText);

    }
    const handleOnChange = (event)=>{
        // console.log("onChange")
        setText(event.target.value);
    }
    const HandleDownClick = ()=>{
      // console.log("Button is click" + text)
      let newText = text.toUpperCase();
      setText (newText);}

  return (
    <div>
      <h3>{prop.heading}</h3>
      <div className='mb-3'>
        <textarea className="postContent" value={text} onChange={handleOnChange} rows={8} cols={80}></textarea>
        </div>
      <div className='mb-3' style={{ display: 'flex', justifyContent: 'space-between',marginRight: '300px' }}>
      <button className="btn btn-primary"onClick={HandleUpClick}>Convert to UpperCase</button>
        <button className="btn btn-primary"onClick={HandleDownClick}>Convert to LowerCase</button>
        </div >
      <h2 >Your Text Summary</h2>

      <p>{text.split(" ").length} Words and {text.length} Charecters</p>
      <p>Can be read in {0.008 * text.split(" ").length} Minutes</p>
      <h4>Preview</h4>
      <p>{text}</p>

    </div>
  );
}
