import React, { useState, useEffect } from 'react';
import Newsitem from './Newsitem';

function News(props) {
  const [articles, setArticles] = useState([]);
  const [page, setPage] = useState(1);

  useEffect(() => {
    console.log('Fetching data for page:', page);
    async function fetchNews() {
      try {
        const response = await fetch("https://newsapi.org/v2/top-headlines?country=in&apiKey=07dd0014afce4ee7a7461c444d75a5db");
        const data = await response.json();
        setArticles(data.articles);
      } catch (error) {
        console.error("Error fetching news:", error);
      }
    }

    fetchNews();
  }, [page]);

  const handleNext = async () => {
    console.log('Next button clicked. Current page:', page);
    try {
      const nextPage = page + 1;
      const response = await fetch(`https://newsapi.org/v2/top-headlines?country=in&apiKey=07dd0014afce4ee7a7461c444d75a5db&page=${nextPage} &pagesize=${props.pageSize}`);
      const data = await response.json();

      setArticles(data.articles);
      setPage(prevPage => nextPage); // Using callback to update the state
    } catch (error) {
      console.error("Error fetching news:", error);
    }
  };

  const handlePrevious = async () => {
    if (page > 1) {
      const newPrevPage = page - 1;
      try {
        const response = await fetch(`https://newsapi.org/v2/top-headlines?country=in&apiKey=07dd0014afce4ee7a7461c444d75a5db&page=${newPrevPage} &pagesize=${props.pageSize}`);
        const data = await response.json();

        setArticles(data.articles);
        setPage(prevPage => newPrevPage); // Using callback to update the state
      } catch (error) {
        console.error("Error fetching news:", error);
      }
    }
  };



  return (
    <div className='container my-3' style={{ color: props.mode === 'dark' ? 'white' : 'dark' }}>
      <h1>Fatafat News Head-lines</h1>

      <div className='row'>
        {articles.map((element) => (
          <div className='col-md-4' key={element.url}>
            <Newsitem
              title={element.title ? element.title.slice(0, 45) : ''}
              description={element.description ? element.description.slice(0, 88) : ''}
              imgUrl={element.urlToImage}
              newsUrl={element.url}
            />
          </div>
        ))}
      </div>
      <div className='container d-flex justify-content-between'>
        <button disabled={page <= 1} type="button" onClick={handlePrevious} className="btn btn-warning">
          &larr; Previous
        </button>
        <button type="button" onClick={handleNext} className="btn btn-warning">
          Next &rarr;
        </button>
      </div>
    </div>
  );
}

export default News;
