import React, { useEffect, useState } from 'react';
import ReactApexChart from 'react-apexcharts';
import Emptable from './employee';

const PieChart = (props) => {
  const [labelColor, setLabelColor] = useState('#000000'); // Default: black

  useEffect(() => {
    // When the props.mode changes, update the labelColor accordingly
    const newLabelColor = props.mode === 'dark' ? '#FFFFFF' : '#000000'; // White for 'dark' mode, black for others
    setLabelColor(newLabelColor);
  }, [props.mode]);

  const options = {
    chart: {
      type: 'pie',
    },
    labels: ['Developers', 'Front-end', 'Testers', 'Others'],
    series: [35, 25, 40, 10],
    dataLabels: {
      style: {
        colors: [labelColor, labelColor, labelColor, labelColor],
      },
    },
  };

  return (
    <div>
      <Emptable />
      <h3>Company Employees Salary Performance</h3>
      <ReactApexChart options={options} series={options.series} type="pie" width="500" />
    </div>
  );
};

export default PieChart;
