import React from 'react';
import ReactApexChart from 'react-apexcharts';
import Emptable from './employee';

const PieChart = (props) => {
  const labelColorClass = props.mode === 'dark' ? 'label-white' : 'label-black';

  const options = {
    chart: {
      type: 'pie',
    },
    labels: ['Developers', 'Front-end', 'Testers', 'Others'],
    series: [35, 25, 40, 10],
  };

  return (
    <div>
      <Emptable />
      <h3>Company Employees Salary Performance</h3>
      <ReactApexChart
        options={options}
        series={options.series}
        type="pie"
        width="500"
        className={labelColorClass} // Add the class dynamically
      />
    </div>
  );
};

export default PieChart;
