import ReactApexChart from 'react-apexcharts';
import Emptable from './employee';

const PieChart = (props) => {
    const data = {
        labels: ['Developers', 'Front-end', 'Testers', 'Others'],
        counts: [35, 25, 40, 10],
    };

    const totalCount = data.counts.reduce((sum, count) => sum + count, 0);
    const percentages = data.counts.map(count => (count / totalCount) * 100);

    const options = {
        chart: {
            type: 'pie',
        },
        labels: data.labels,
        series: data.counts,
    };

    return (
        <div style={{ color: props.mode === 'dark' ? 'white':'red' }}>
            <Emptable/>
            <h3>Company Employees Salary performance</h3>
            <ReactApexChart options={options} series={percentages} type="pie" width="500" />
        </div>
    );
};

export default PieChart;
